import React, { useState } from "react";
import MyTable from "./MyTable"; // MyTable 컴포넌트의 경로를 정확히 설정해야 합니다.

export default function DoRegistration({inputText}) {
  // 기존 데이터 배열
  const [data, setData] = useState([
    {
      id: 1,
      subject: "Math",
      instructor: "John Doe",
      credits: 3,
    },
    {
      id: 2,
      subject: "Science",
      instructor: "Jane Smith",
      credits: 3,
    },
    // 기존 데이터...
  ]);
 

  // 새로운 과목 추가 함수
  const addSubject = () => {
    const newData = [
      ...data,
      {
        id: data.length + 1, // 현재 데이터 개수 + 1 로 ID 설정
        subject: "New Subject",
        instructor: "New Instructor",
        credits: 3,
        // 필요한 경우 기본 값으로 설정 가능
      },
    ];
    setData(newData);
  };
  useEffect(() => {
    if (inputText.length === 4) {
      addSubject();
    }
  }, [inputText]);
  const columns = React.useMemo(
    () => [
      {
        Header: "No",
        accessor: "id",
      },
      {
        Header: "삭제",
        accessor: "delete", // 삭제 버튼을 추가하려면 적절한 accessor를 지정해야 합니다.
        // 삭제 버튼을 추가하는 방법은 여러 가지가 있습니다. 예를 들어, 커스텀 셀 렌더링을 사용할 수 있습니다.
        // 커스텀 셀 렌더링에 대한 예제는 react-table 공식 문서에서 찾을 수 있습니다.
      },
      {
        Header: "학년",
        accessor: "grade",
      },
      {
        Header: "이수구분",
        accessor: "classification",
      },
      {
        Header: "과목번호",
        accessor: "courseCode",
      },
      {
        Header: "교과목명",
        accessor: "subjectName",
      },
      {
        Header: "학점",
        accessor: "credits",
      },
      {
        Header: "시간",
        accessor: "time",
      },
      {
        Header: "강의시간",
        accessor: "lectureTime",
      },
      {
        Header: "담당교수",
        accessor: "professor",
      },
    ],
    []
  );

  return (
    <div style={{ display: "flex", marginLeft: "312px" }}>
      <button
        style={{
          backgroundColor: "#3BAAB5",
          border: "0px",
          height: "15px",
          marginTop: "13px",
        }}
      ></button>
      <div
        style={{
          display: "flex",
          flexDirection: "column",
        }}
      >
        <text style={{ fontWeight: "bold", marginTop: "10px", marginLeft: "10px" }}>수강신청 내역</text>
        <MyTable columns={columns} data={data} />
        
    
      </div>
    </div>
  );
}
