import konkukPic from "../image/ku_pics.png"
import "../index.css"
import Notice from "./Notice";
export default function Header(){

    return (<>
        <div style={{ display: "flex", alignItems: "center" }} className="konkuk_pic">
          <img
            style={{
              width: "103px",
              height: "37px",
              marginTop: "14px",
              marginLeft: "18px",
              marginRight: "8px", // 이미지와 텍스트 사이 간격
            }}
            alt="konkuk_pic"
            src={konkukPic}
          />
          <span style={{ 
            textAlign: "center",
            marginTop:15,
            fontSize:18,
            fontFamily:"Bold",
            whiteSpace: "nowrap" 
         }}>수강신청연습</span>
           <Notice/>
           <button style={{
                backgroundColor:"transparent",
                border: "none",
                borderBottom: "3px solid #28a745", // 아래쪽에만 3px 초록색 테두리
                padding: "10px 20px", // 버튼 내부 여백
                cursor: "pointer", 
                fontSize:17,
                whiteSpace: "nowrap" 
            }}>수강 신청</button>
        </div>
        <hr/>
        </>
    
      );
}//103 30
