import React, { useState } from "react";
import DoRegistration from "./DoRegistration";

export default function RegistrationClicked() {
  const [buttonName, setButtonName] = useState("register");
  const [inputText, setInputText] = useState("");

  const handleButtonClick = (buttonName) => {
    setButtonName(buttonName);
  };

  const activeButton = () => {
    alert(`${inputText} 입력 완료`);
  };

  const activeEnter = (e) => {
    if (e.key === "Enter") {
      activeButton();
    }
  };

  const addSubject = () => {
    // 신규 과목 추가 로직 구현
    // 예를 들어, 입력된 과목번호를 기반으로 새로운 과목을 만들고 배열에 추가하는 방식 등
  };

  return (
    <div>
      <div style={{ display: "flex" }}>
        {/* 버튼들 */}
      </div>

      {/* 입력창 및 신청 버튼 */}

      <div style={{ borderBottom: "1px solid black", lineHeight: "0.1em", width: "1180px", marginLeft: "312px" }} />
      <DoRegistration addSubject={addSubject} />
    </div>
  );
}
