import konkukPic from "../../image/ku_pics.png"
import Notice from "./Notice";
import Registartion from "./registration";
import Check from "./check"
import { useState } from "react";
import "../../index.css"
import Evaluation from "./Evaluation";
export default function Header(){
  
  const [activeButton, setActiveButton] = useState('Notice');

  const handleButtonClick = (componentName) => {
    setActiveButton(componentName);
  };

    return (
    <nav>
        <div style={{ position:"relative" ,display: "flex"}} className="konkuk_pic">
          <img
            className="ku_pic"
            alt="konkuk_pic"
            src={konkukPic}
          />
          <span className="konkuk_pic_text">수강신청연습</span>
           
          <Notice             
           onClick={() => handleButtonClick('Notice')}
           style={{ borderBottom: activeButton === 'Notice' ? '3px solid #28a745' : 'none' ,marginLeft:"400px"}}/>
           <Registartion
           onClick={() => handleButtonClick('Registartion')}
           style={{ borderBottom: activeButton === 'Registartion' ? '3px solid #28a745' : 'none' }}/>
           
           <Check
           onClick={() => handleButtonClick('Check')}
           style={{ borderBottom: activeButton === 'Check' ? '3px solid #28a745' : 'none' }}/>
           <Evaluation
           onClick={() => handleButtonClick('Evaluation')}
           style={{ borderBottom: activeButton === 'Evaluation' ? '3px solid #28a745' : 'none' }}/>
           
        </div>
        <div style={{
    backgroundImage: "linear-gradient(to right, #ddd, #999)", // Gradient from light gray to dark gray
    borderBottom: "3px solid",
    lineHeight: "0.1em",
    width: "1510px",
}}/>

        </nav>
        
    
      );
}
